fileContent:
body {
  margin: 0px;
  padding: 0px;
}


.split.split-horizontal, .gutter.gutter-horizontal {
    height: 100%;
    overflow: auto;
    float: left;
}
.gutter.gutter-horizontal {
    cursor: ew-resize;
}
.gutter {
    background-color: #2b3a42;
    background-repeat: no-repeat;
    background-position: 50%;
}
.window {
  position: absolute;
  bottom: 0;
  top: 0;
  left: 0;
  right: 0;
  overflow: auto;
  border: 1px solid #ddd;
  border-radius: 4px;
}

.fileTree {
  height: 100%;
  width: 100%;
  background-color: #2b3a42;
}
.split-horizontal{
  background-color: #273238;
}

.split.split-horizontal, .gutter.gutter-horizontal {
  float: left;
}

.split.split-horizontal, .gutter.gutter-horizontal {
  float: left;
}
#project-tree> li{
  margin-right: auto;
  margin-left: auto;
  text-align: center;
}

/**Addition**/
.tree-leaf{position:relative}.tree-leaf
.tree-child-leaves{display:block;margin-left:15px}
.tree-leaf .hidden{display:none;visibility:hidden}
.tree-leaf .tree-expando{
  background:#ddd;
  border-radius:3px;
  cursor:pointer;
  float:left;
  height:10px;
  line-height:10px;
  position:relative;
  text-align:center;
  top:5px;width:10px
  }
.tree-leaf .tree-expando:hover{background:#aaa}
.tree-leaf .tree-leaf-text{

  cursor:pointer;
  float:left
  ;margin-left:5px
  }

  .tree-leaf .tree-leaf-text:hover{color:#00f}
  .tree-leaf .tree-leaf-content:after,.tree-leaf .tree-leaf-content:before{
    content:" ";
    display:table;
    }

  .tree-leaf .tree-leaf-content:after{clear:both}

  .nav-button {
    width: 20vw;
    margin: 0px;
    outline: none;
    border-top: none;
    border-bottom: none;
    border-radius: 0px;
  }
  .code-workspace {
    height: 100vw;
    display: none;
  }
  .CodeMirror {
    border: 1px solid #eee;
    min-height: 100%;
  }

  .project-header {
    color: #eee;
    width: 100%;
    text-align: center;
    padding-top: 10px;
    padding-bottom: 10px;

  }

.footer-editor {
  width: 100%;
  height: 3vh;
  background-color: #fff;
}

#jstree_demo_div {
  color: #ddd;
}
.elem{
  color: #fff;
}
